const mpesa = require("mpesa-node-api");

exports.getMpesa = async (req, res) => {

    const numero = req.body.numero;
    const quantia = req.body.quantia;

    mpesa.initializeApi({
    baseUrl: "api.sandbox.vm.co.mz",
    apiKey: "xb7lpkyuv7erw37thmtjmcm4byyr18a0",
    publicKey: "",
    origin: "developer.mpesa.vm.co.mz",
    serviceProviderCode: "171717"
});

  mpesa.initiate_c2b(quantia, `258${numero}`,'T12344C', 'ref1')
  .then(function(response) {
    console.log(response)
    res.status(200).json("pass")
  })
  .catch(function(error) {
    console.log(error)
    res.status(400).json(error.message)
  });
}